users.map:
function(doc) {
  if (doc.type.indexOf("/type/user") >= 0) {
    emit(doc.username.toLowerCase(), doc);
    emit(doc.name, doc);
  }
}

collaborators.map:
function(doc) {
  if (doc.type.indexOf("/type/collaborator") >= 0 && doc.user) {
    emit([doc.user, doc.document], doc);
  }
}

documents_by_keyword.map:
function(doc) {
  if (doc.type.indexOf("/type/document") >= 0) {
    emit(doc.title, doc);
    emit(doc.name, doc);
  }
}

notifications.map:
function(doc) {
  if (doc.type.indexOf("/type/notification") >= 0) {

    emit([doc.recipient, doc.created_at], doc);
    // emit(doc.username.toLowerCase(), doc);
    // emit(doc.name, doc);
  }
}
